import React from 'react';
import { Link, useLocation } from 'react-router-dom';
import styled from 'styled-components';

const SidebarContainer = styled.div`
  width: 240px;
  background: #1e1e2f;
  color: #fff;
  display: flex;
  flex-direction: column;
  padding: 20px 0;
  height: 100vh;
  position: fixed;
  left: 0;
  top: 0;
`;

const SidebarHeader = styled.div`
  padding: 0 20px 20px 20px;
  font-size: 20px;
  font-weight: bold;
  color: #fff;
  border-bottom: 1px solid #333;
`;

const Menu = styled.div`
  display: flex;
  flex-direction: column;
  margin-top: 20px;
`;

const MenuItem = styled(Link)<{ $active?: boolean }>`
  padding: 12px 20px;
  margin: 4px 0;
  color: ${(props) => (props.$active ? '#fff' : '#bbb')};
  background: ${(props) => (props.$active ? '#4361ee' : 'transparent')};
  text-decoration: none;
  transition: background 0.3s, color 0.3s;

  &:hover {
    background: #3a0ca3;
    color: #fff;
  }
`;

const Sidebar: React.FC = () => {
  const { pathname } = useLocation();

  return (
    <SidebarContainer>
      <SidebarHeader>Admin Panel</SidebarHeader>
      <Menu>
        <MenuItem to="/admin/dashboard" $active={pathname.includes('/dashboard')}>
          Dashboard
        </MenuItem>
        <MenuItem to="/admin/users" $active={pathname.includes('/users')}>
          Users
        </MenuItem>
        <MenuItem to="/admin/reports" $active={pathname.includes('/reports')}>
          Reports
        </MenuItem>
      </Menu>
    </SidebarContainer>
  );
};

export default Sidebar;
