import React, { useEffect, useState } from 'react';
import styled from 'styled-components';
import { useSelector } from 'react-redux';
import { useAppDispatch } from '../store/hooks';
import { RootState } from '@/store';
import { fetchClients } from '../store/clientsSlice';

const Container = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  width: 100%;
  min-height: calc(100vh - 64px);
  padding: 2rem;
  box-sizing: border-box;
  background: #f5f6fa;
`;

const Title = styled.h2`
  margin-bottom: 1.5rem;
  color: #333;
  text-align: center;
`;

const TableWrapper = styled.div`
  width: 100%;
  max-width: 1200px;
  overflow-x: auto;
`;

const Table = styled.table`
  width: 100%;
  border-collapse: collapse;
  background: #fff;
  border-radius: 8px;
  overflow: hidden;
  box-shadow: 0 2px 8px rgba(0,0,0,0.1);
  
  // Consistent row heights
  tr {
    height: auto;
    min-height: 48px;
  }
`;

interface ExpandedInfoProps {
  hasDetails: boolean;
}

const ExpandedInfo = styled.div<ExpandedInfoProps>`
  width: 100%;
  color: ${({ hasDetails }) => (hasDetails ? '#1E1E2F' : 'orange')};
`;

const Th = styled.th`
  text-align: left;
  padding: 12px;
  background: #f4f6f8;
  border-bottom: 1px solid #ddd;
  font-weight: 600;
`;

const Td = styled.td`
  padding: 12px;
  border-bottom: 1px solid #eee;
  font-size: 14px;
`;

interface ExpandedTdProps {
  isVisible: boolean;
}

const ExpandedTd = styled.td<ExpandedTdProps>`
  padding: ${props => props.isVisible ? '12px' : '0 12px'};
  background: #f9f9f9;
  border-bottom: 1px solid #ddd;
  font-size: 14px;
  height: ${props => props.isVisible ? 'auto' : '0'};
  min-height: ${props => props.isVisible ? '40px' : '0'};
  opacity: ${props => props.isVisible ? '1' : '0'};
  transition: all 0.2s ease-in-out;
  overflow: hidden;
  visibility: ${props => props.isVisible ? 'visible' : 'hidden'};
`;

interface TableRowProps {
  isExpanded: boolean;
}

const TableRow = styled.tr<TableRowProps>`
  cursor: pointer;
  transition: background-color 0.2s ease;
  
  &:hover {
    background-color: #f8f9fa;
  }
  
  ${props => props.isExpanded && `
    background-color: #f0f2f5;
    border-bottom: 2px solid #e0e0e0;
  `}
`;

const ExpandedRow = styled.tr<{ isVisible: boolean }>`
  ${props => props.isVisible && `
    background-color: #f9f9f9;
  `}
`;

const Clients: React.FC = () => {
  const dispatch = useAppDispatch();
  const { clients, loading, error } = useSelector((state: RootState) => state.clients);
  const [expandedRow, setExpandedRow] = useState<number | null>(null);

  const toggleRow = (index: number) => {
    setExpandedRow(prev => (prev === index ? null : index));
  };

  useEffect(() => {
    dispatch(fetchClients());
  }, [dispatch]);

  if (loading) return <Container><p>Loading clients...</p></Container>;
  if (error) return <Container><p style={{ color: 'red' }}>{error}</p></Container>;

  return (
    <Container>
      <Title>Clients</Title>
      <TableWrapper>
        <Table>
          <thead>
            <tr>
              <Th>Name</Th>
              <Th>Sirname</Th>
              <Th>Mail</Th>
              <Th>Phone</Th>
              <Th>Active</Th>
              <Th>Created</Th>
              <Th>Update</Th>
            </tr>
          </thead>
          <tbody>
            {clients.map((client, index) => (
              <React.Fragment key={index}>
                <TableRow 
                  onClick={() => toggleRow(index)} 
                  isExpanded={expandedRow === index}
                >
                  <Td>{client.firstName}</Td>
                  <Td>{client.lastName}</Td>
                  <Td>{client.email}</Td>
                  <Td>{client.phone || '-'}</Td>
                  <Td>{client.isActive ? 'Yes' : 'No'}</Td>
                  <Td>{new Date(client.createdAt).toLocaleString()}</Td>
                  <Td>{new Date(client.updatedAt).toLocaleDateString()}</Td>
                </TableRow>

                <ExpandedRow isVisible={expandedRow === index}>
                  <ExpandedTd colSpan={7} isVisible={expandedRow === index}>
                    <ExpandedInfo hasDetails={!!client.address && Object.keys(client.address).length > 0}>
                      <strong>Address: </strong>
                      {client.address
                        ? `${client.address.street || '-'}, ${client.address.city || '-'}, ${client.address.state || '-'}, ${client.address.country || '-'}, ${client.address.zipCode || '-'}`
                        : 'No address'}
                    </ExpandedInfo>
                  </ExpandedTd>
                </ExpandedRow>
              </React.Fragment>
            ))}
          </tbody>
        </Table>
      </TableWrapper>
    </Container>
  );
};

export default Clients;
