import React, { useEffect, useState } from 'react';
import styled from 'styled-components';
import { useSelector } from 'react-redux';
import { useAppDispatch } from '../store/hooks';
import { RootState } from '@/store';
import { fetchClients } from '../store/clientsSlice';

const Container = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  width: 100%;
  min-height: calc(100vh - 64px);
  padding: 2rem;
  box-sizing: border-box;
  background: #f5f6fa;
`;

const Title = styled.h2`
  margin-bottom: 1.5rem;
  color: #333;
  text-align: center;
`;

const TableWrapper = styled.div`
  width: 100%;
  max-width: 1200px;
  overflow-x: auto;
`;

const Table = styled.table`
  width: 100%;
  table-layout: fixed; /* prevents column width recalculation -> no horizontal shake */
  border-collapse: collapse;
  background: #fff;
  border-radius: 8px;
  overflow: hidden;
  box-shadow: 0 2px 8px rgba(0,0,0,0.1);
`;

interface ExpandedInfoProps {
  hasDetails: boolean;
}

const ExpandedInfo = styled.div<ExpandedInfoProps>`
  width: 100%;
  color: ${({ hasDetails }) => (hasDetails ? '#1E1E2F' : 'orange')};
  word-wrap: break-word;
`;

/* the element that will expand/collapse smoothly */
const ExpandContent = styled.div<{ open?: boolean }>`
  max-height: ${({ open }) => (open ? '260px' : '0')};
  overflow: hidden;
  transition: max-height 220ms ease, opacity 180ms ease, padding 180ms ease;
  opacity: ${({ open }) => (open ? 1 : 0)};
  padding: ${({ open }) => (open ? '12px 0' : '0')};
  box-sizing: border-box;
`;

const Th = styled.th`
  text-align: left;
  padding: 12px;
  background: #f4f6f8;
  border-bottom: 1px solid #ddd;
  font-weight: 600;
`;

const Td = styled.td`
  padding: 12px;
  border-bottom: 1px solid #eee;
  font-size: 14px;
  white-space: nowrap;
  text-overflow: ellipsis;
  overflow: hidden;
`;

const ExpandedTd = styled.td`
  padding: 0; /* padding handled inside ExpandContent to avoid instant jump */
  background: #f9f9f9;
  border-bottom: 1px solid #ddd;
  font-size: 14px;
`;

const Clients: React.FC = () => {
  const dispatch = useAppDispatch();
  // if your state is clients.data use that, otherwise adapt
  const clients = useSelector((state: RootState) => state.clients);
  const loading = clients.loading;
  const error = clients.error;
  const list = Array.isArray(clients) ? clients : (clients.data || clients); // defensive
  const [expandedRow, setExpandedRow] = useState<number | null>(null);

  const toggleRow = (index: number) => {
    setExpandedRow(prev => (prev === index ? null : index));
  };

  useEffect(() => {
    dispatch(fetchClients());
  }, [dispatch]);

  if (loading) return <Container><p>Loading clients...</p></Container>;
  if (error) return <Container><p style={{ color: 'red' }}>{error}</p></Container>;

  return (
    <Container>
      <Title>Clients</Title>
      <TableWrapper>
        <Table>
          {/* fixed column widths so the table never reflows widths on expand */}
          <colgroup>
            <col style={{ width: '14%' }} />
            <col style={{ width: '14%' }} />
            <col style={{ width: '28%' }} />
            <col style={{ width: '12%' }} />
            <col style={{ width: '8%' }} />
            <col style={{ width: '12%' }} />
            <col style={{ width: '12%' }} />
          </colgroup>

          <thead>
            <tr>
              <Th>Name</Th>
              <Th>Sirname</Th>
              <Th>Mail</Th>
              <Th>Phone</Th>
              <Th>Active</Th>
              <Th>Created</Th>
              <Th>Update</Th>
            </tr>
          </thead>

          <tbody>
            {list.map((client: any, index: number) => (
              <React.Fragment key={index}>
                <tr onClick={() => toggleRow(index)} style={{ cursor: 'pointer' }}>
                  <Td title={client.firstName}>{client.firstName}</Td>
                  <Td title={client.lastName}>{client.lastName}</Td>
                  <Td title={client.email}>{client.email}</Td>
                  <Td>{client.phone || '-'}</Td>
                  <Td>{client.isActive ? 'Yes' : 'No'}</Td>
                  <Td>{new Date(client.createdAt).toLocaleString()}</Td>
                  <Td>{client.updatedAt ? new Date(client.updatedAt).toLocaleDateString() : '-'}</Td>
                </tr>

                <tr>
                  <ExpandedTd colSpan={7}>
                    {/* ExpandContent is always present but collapses via max-height -> no abrupt reflow */}
                    <ExpandContent open={expandedRow === index}>
                      <ExpandedInfo hasDetails={!!client.address && Object.keys(client.address).length > 0}>
                        <strong>Address: </strong>
                        {client.address
                          ? `${client.address.street || '-'}, ${client.address.city || '-'}, ${client.address.state || '-'}, ${client.address.country || '-'}, ${client.address.zipCode || '-'}`
                          : 'No address'}
                      </ExpandedInfo>
                    </ExpandContent>
                  </ExpandedTd>
                </tr>
              </React.Fragment>
            ))}
          </tbody>
        </Table>
      </TableWrapper>
    </Container>
  );
};

export default Clients;
