import React, { useEffect } from 'react';
import styled from 'styled-components';
import { useSelector } from 'react-redux';
import { useAppDispatch } from '../store/hooks';

import { RootState } from '@/store';
import { fetchPatients } from '../store/patientsSlice';

const Container = styled.div`
  padding: 2rem;
  margin-left: 240px; /* match Sidebar width */
`;

const Title = styled.h2`
  margin-bottom: 1.5rem;
  color: #333;
`;

const Table = styled.table`
  width: 100%;
  border-collapse: collapse;
  background: #fff;
  border-radius: 8px;
  overflow: hidden;
  box-shadow: 0 2px 8px rgba(0,0,0,0.1);
`;

const Th = styled.th`
  text-align: left;
  padding: 12px;
  background: #f4f6f8;
  border-bottom: 1px solid #ddd;
  font-weight: 600;
`;

const Td = styled.td`
  padding: 12px;
  border-bottom: 1px solid #eee;
  font-size: 14px;
`;

const Patients: React.FC = () => {
  const dispatch = useAppDispatch();
  const { patients, loading, error } = useSelector((state: RootState) => state.patients);
    console.log("===>", patients)
  useEffect(() => {
    dispatch(fetchPatients());
  }, [dispatch]);

  if (loading) return <Container><p>Loading patients...</p></Container>;
  if (error) return <Container><p style={{color: 'red'}}>{error}</p></Container>;

  return (
    <Container>
      <Title>Patients</Title>
      <Table>
        <thead>
          <tr> 
            <Th>name</Th>
            <Th>species</Th>
            <Th>breed</Th>
            <Th>isActive</Th>
            <Th>created</Th>
            <Th>DOB</Th>

          </tr>
        </thead>
        <tbody>
          {patients.map((patient, index) => (
            <tr key={index}> 
              <Td>{patient.name}</Td>
              <Td>{patient.species}</Td>
              <Td>{patient.breed}</Td>
              <Td>{patient.isActive}</Td> 
              <Td>{patient.createdAt.toLocaleDateString()}</Td> 
              <Td>{patient.dateOfBirth.toLocaleString()}</Td> 
              
            </tr>
          ))}
        </tbody>
      </Table>
    </Container>
  );
};

export default Patients;
